query:
CREATE TABLE IF NOT EXISTS `review_responses` (
  `id` int AUTO_INCREMENT NOT NULL,
  `reviewId` int NOT NULL,
  `userId` int NOT NULL,
  `content` text NOT NULL,
  `createdAt` timestamp NOT NULL DEFAULT (now()),
  `updatedAt` timestamp NOT NULL DEFAULT (now()) ON UPDATE CURRENT_TIMESTAMP,
  CONSTRAINT `review_responses_id` PRIMARY KEY(`id`),
  CONSTRAINT `review_responses_reviewId_fk` FOREIGN KEY (`reviewId`) REFERENCES `product_reviews`(`id`),
  CONSTRAINT `review_responses_userId_fk` FOREIGN KEY (`userId`) REFERENCES `users`(`id`)
);

CREATE INDEX IF NOT EXISTS `review_id_idx` ON `review_responses` (`reviewId`);
CREATE INDEX IF NOT EXISTS `user_id_idx` ON `review_responses` (`userId`);

CREATE TABLE IF NOT EXISTS `review_reminder_log` (
  `id` int AUTO_INCREMENT NOT NULL,
  `orderId` int NOT NULL,
  `userId` int NOT NULL,
  `productId` int NOT NULL,
  `sentAt` timestamp NOT NULL DEFAULT (now()),
  `emailStatus` enum('sent','failed','bounced') NOT NULL DEFAULT 'sent',
  CONSTRAINT `review_reminder_log_id` PRIMARY KEY(`id`),
  CONSTRAINT `review_reminder_log_orderId_fk` FOREIGN KEY (`orderId`) REFERENCES `orders`(`id`),
  CONSTRAINT `review_reminder_log_userId_fk` FOREIGN KEY (`userId`) REFERENCES `users`(`id`),
  CONSTRAINT `review_reminder_log_productId_fk` FOREIGN KEY (`productId`) REFERENCES `products`(`id`)
);

CREATE INDEX IF NOT EXISTS `order_id_idx` ON `review_reminder_log` (`orderId`);
CREATE INDEX IF NOT EXISTS `user_id_idx_reminder` ON `review_reminder_log` (`userId`);
CREATE INDEX IF NOT EXISTS `product_id_idx` ON `review_reminder_log` (`productId`);

